import React from "react";
import FormMail from "../admin/FormMail";
import storeSearchResult from "../store/storeSearchResult";
import axios from "axios";
//material-ui 쓸꺼얌
//import { makeStyles } from "@material-ui/core/styles";
import FormHelperText from "@material-ui/core/FormHelperText";
import Select from "@material-ui/core/Select";
import TextField from "@material-ui/core/TextField";
import { Button } from "@material-ui/core";
import SearchIcon from "@material-ui/icons/Search";
import FormControl from "@material-ui/core/FormControl";


// user 1 로그인 했다고 가정하기
const user1 = "1";

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isModalOpen: false,
      showSearchResult: false,
      storeName: "",
      cuisine: "",
      list: []
    };
  }

  // component load 되면 로그인 했다고 가정하고 session에 user 1의 userNo 넣기
  componentDidMount () {
    window.sessionStorage.setItem("userNo", user1)
  }


  openModal = () => {
    this.setState({
      isModalOpen: true
    });
  };

  closeModal = () => {
    this.setState({
      isModalOpen: false
    });
  };

  onChange = e => this.setState({ [e.target.name]: e.target.value });

  search = async () => {
    if (this.state.storeName !== null && this.state.cuisine !== null) {
      const storeName = this.state.storeName;
      const cuisine = this.state.cuisine;

      let params = new FormData();
      params.append("storeName", storeName);
      params.append("cuisine", cuisine);

      await axios({
        method: "get",
        url: "searchBothOfThem",
        data: params
      })
        .then(success => {
          const data = success.data;
          console.log("음식점 이름 & 음식종류로 검색 성공 크크.. " + data);
          this.setState({
            list: data,
            storeName: "",
            cuisine: "",
            showSearchResult: true
          });
        })
        .catch(error => console.log("에러..." + error));
    } else if (this.state.cuisine === null) {
      const storeName = this.state.storeName;
      await axios({
        method: "get",
        url: `searchByName/${storeName}`
      }).then(success => {
        const data = success.data;
        console.log("음식점 이름으로 검색 성공 " + data);
        this.setState({
          list: data,
          storeName: "",
          showSearchResult: true
        }).catch(error => console.log("에러..흑흑.. => " + error));
      });
    } else if (this.state.storeName === null) {
      const cuisine = this.state.cuisine;
      await axios({
        method: "get",
        url: `searchByCuisine/${cuisine}`
      }).then(success => {
        const data = success.data;
        console.log("음식 종류로 검색 성공 " + data);
        this.setState({
          list: data,
          storeName: "",
          showSearchResult: true
        }).catch(error => console.log("에러..흑흑.. => " + error));
      });
    }
  };

  render() {
    // const classes = useStyles();
    return (
      <div>
        <h4>메인페이지</h4>
        <div>
          {/* 검색 */}
          <form /*className={classes.root}*/ noValidate autoComplete="off">
            <TextField
              type="text"
              id="outlined-basic"
              margin="normal"
              label="Outlined"
              variant="outlined"
              placeholder="어느 음식점에서"
              name="storeName"
              onChange={this.onChange}
              value={this.state.storeName}
            />
            <FormControl component="fieldset">
              <Select
                placeholder="어떤 음식을 드실건가요"
                name="cuisine"
                value={this.state.cuisine}
                onChange={this.onChange}
                defaultValue = ""
              >
                <option value="">None</option>
                <option value="51">한식</option>
                <option value="52">양식</option>
                <option value="53">중식</option>
                <option value="54">일식</option>
                <option value="55">동남아식</option>
                <option value="56">뷔페식</option>
              </Select>
              <FormHelperText>음식종류 고르긔</FormHelperText>
            </FormControl>
            <Button variant="contained" color="primary" onClick={this.search}>
              <SearchIcon />
            </Button>
          </form>
        </div>
        {this.state.showSearchResult && (
          <storeSearchResult data={this.state.list} />
        )}{""}

        {/* 폼메일 보내기 */}
        <Button onClick={this.openModal}>Email Us</Button>
        {this.state.isModalOpen && <FormMail onClose={this.closeModal} />} {""}
      </div>
    );
  }
}

// const useStyles = makeStyles(theme => ({
//   root: {
//     "& > *": {
//       margin: theme.spacing(1),
//       width: 200
//     }
//   }
// }));

export default Main;
